//Dependencies
import React from 'react';
import { Icon } from 'react-materialize';
import Table from 'react-bootstrap/Table'
import map from 'lodash/map';
//Internals
import PRODUCTS from '../../Data';

const AllItems = () => (

  <body>
  <div className="items">
    {map(PRODUCTS, (product)=> (
      <Table striped bordered hover size="sm" striped>
        <thead>
          <tr>
            <th>Image</th>
            <th><u><h5 id="product-name">{product.name}</h5></u></th>
          </tr>
        </thead>
        <tbody>
          <tr hover>
            <td align="center"><img alt={product.name} src={product.img} width="150" height="200"/></td>
            <td align="center">
              <h6 id="product-description">{product.description}</h6>
              <h7 id="product-price">${product.price}</h7>
              <Icon small id="add-icon">add_shopping_cart</Icon>
            </td>
          </tr>
        </tbody>
      </Table>
    ))}
  </div>
  </body>
)

export default AllItems;
