import React from 'react';
import '../../All.css';
import PRODUCTS from '../Data';
import AllItems from '../Items/AllItems'

var Filtered = PRODUCTS;
class Search extends  React.Component {

	constructor( props ) {
		super( props );
		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	Filter = (query) => {
		var count = 0
		Filtered = []
		console.log("inside Filter:  "+query);
		if(query){
			console.log("inside filter if:  ");
			for(var i = 0; i < PRODUCTS.length; i++) {
				if(PRODUCTS[i].name.includes(query)){
					Filtered[count] = PRODUCTS[i];
					count = count +1;
				}
			}
			console.log("inside filter if last:  "+Filtered.length);
		}
		else{
			Filtered = PRODUCTS;
			console.log("inside filter else:  "+Filtered[0]+Filtered[1]+Filtered[2]);
		}
	  }

	forceUpdateHandler(){
		AllItems.forceUpdate();
	};

	handleOnClick = (event) => {
		const query = document.getElementById('search-input').value;
		console.log("inside handle:  "+query);
		this.Filter(query);
		this.forceUpdateHandler();
	};

	render() {
		return (
			<div className="container">
				<label className="search-label" htmlFor="search-input">
					<input
						type="text"
						id="search-input"
						placeholder="Search..."
					/>
					<i className="fa fa-search search-icon" onClick={this.handleOnClick}/>
				</label>

			</div>
			)
	}
}
export default Search;
export {Filtered};
