import React from 'react';
import Axios from 'axios';
//Internal
import '../../../All.css';

class AllItems extends React.Component{
  constructor( props ) {
		super( props );
		this.state = {
			query: '',
      results: [],
      loading: false,
      message: '',
      page:0
		};
	}

  fetchSearchResults = (updatedPageNo = '', query ) => {
    const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';
    const searchUrl = `http://localhost:8080/mobiles/?s=${query}&offset=${pageNumber}`;    // By default the limit of results is 20

    if (this.cancel) {
      this.cancel.cancel();   // Cancel the previous request before making a new request
    }
    this.cancel = Axios.CancelToken.source();   // Create a new CancelToken

    Axios
      .get(searchUrl, {
        cancelToken: this.cancel.token,
      })
      .then((res) => {
        this.setState({
          results: res.data,
          loading: false,
        });
      })
      .catch((error) => {
        if (Axios.isCancel(error) || error) {
          this.setState({
            loading: false,
            message: 'Failed to fetch results.Please check network',
          });
        }
      });
  };

  handleOnInputChange = (event) => {
    const query = event.target.value;
    if ( ! query ) {
      Axios.get('http://localhost:8080/mobiles/').then(res=>{
          const mobileData = res.data;
          this.setState({results:mobileData});
          console.log(this.state.results);
      })
    } 
    else {

      this.setState({ query, loading: true, message: '' }, () => {
        this.fetchSearchResults(1, query);
      });
    }
  };

  handleOnClick(option){
    const query = this.state.query;
    if (option ==  0 && this.state.page>0) {
      this.fetchSearchResults(this.state.page-1, query);
    } 
    else if(option ==  0 && this.state.page < this.state.results.length/5){
      this.fetchSearchResults(this.state.page+1, query);
    }
  };

  renderSearchResults = () => {
    const result = this.state.results;
    if (result.length) {
      return (
        <div className="results-container">
          {result.map((alldata) => {
            return (
              <tr>
                <td><img alt={alldata.name} src={alldata.image} width="250" height="200"/></td>
                <td>
                  <u><h4 id="product-name">{alldata.brand} {alldata.model} </h4></u>
                  <h5 id="product-description">{alldata.memories} GB</h5>
                  <h5 id="product-price">${alldata.price}</h5>
                </td>
              </tr>
            );
          })}
        </div>
      );
    }
  };

  componentWillMount(){
      Axios.get('http://localhost:8080/mobiles/').then(res=>{
          const mobileData = res.data;
          this.setState({results:mobileData})
      })
  }

    render(){
      return(
        <div>
          <div className="container">
            <label className="search-label" htmlFor="search-input">
              <input
                type="text"
                id="search-input"
                placeholder="Search..."
                onChange={this.handleOnInputChange}
              />
              <i className="fa fa-search search-icon"/>
            </label>
          </div>

          <div>
          {this.renderSearchResults()}
          </div>

          <h6 className="last-page" onClick={this.handleOnClick(0)}>Previous</h6><h6 className="next-page" onClick={this.handleOnClick(1)}>Next</h6>

        </div>
      )}
}


export default AllItems;
