import React from 'react';
import '../../All.css';
import {Link} from 'react-router-dom'

class Search extends  React.Component {
	constructor( props ) {
		super( props );
		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	handleOnInputChange = (event) => {
		const query = document.getElementById('search-input').value;
		document.getElementById('modified-link').to = 'http://localhost:3000/?q='+query;
	};

	render() {
		return (
			<div className="container">
				<label className="search-label" htmlFor="search-input">
					<input
						type="text"
						id="search-input"
						placeholder="Search..."
					/>

					<Link id="modified-link" to='http://localhost:3000/?q='>
					<i className="fa fa-search search-icon" onClick={this.handleOnInputChange}/>
					</ Link>
				</label>

			</div>
			)
	}
}
export default Search;
