import React from 'react';
import '../../All.css';
import {Link} from 'react-router-dom'
import PRODUCTS from '../Data';

import {useLocation} from "react-router-dom";

class Search extends  React.Component {
	constructor( props ) {
		super( props );
		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	Filter = () => {
		var count = 0
		var Filtered = []
		let query = new URLSearchParams(useLocation().search);
		if(!query){
			var quer = query.get("q");
			for(var i = 0; i < PRODUCTS.length; i++) {
				if(PRODUCTS[i].name.includes(quer)){
					Filtered[0] = PRODUCTS[i];
					count = count +1;
				}
			}
		}
		else{
			Filtered = PRODUCTS
		}
	  }

	handleOnInputChange = (event) => {
		const query = document.getElementById('search-input').value;
		document.getElementById('modified-link').to = 'http://localhost:3000/?q='+query;
		this.Filter()
	};

	render() {
		return (
			<div className="container">
				<label className="search-label" htmlFor="search-input">
					<input
						type="text"
						id="search-input"
						placeholder="Search..."
					/>

					<Link id="modified-link" to='http://localhost:3000/?q='>
					<i className="fa fa-search search-icon" onClick={this.handleOnInputChange}/>
					</ Link>
				</label>

			</div>
			)
	}
}
export default Search;
