import React from 'react';
import '../../All.css';
import axios from 'axios';
import {Link} from 'react-router-dom'

class Search extends  React.Component {
	constructor( props ) {
		super( props );
		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	fetchSearchResults = (query) => {

		const searchUrl = `http://localhost:3000/?q=${query}`;  // By default the limit of results is 20

		if (this.cancel) {
			this.cancel.cancel();
		}
		this.cancel = axios.CancelToken.source();

		axios
			.get(searchUrl, {
				cancelToken: this.cancel.token,
			})
			.then((res) => {
				const resultNotFoundMsg = !res.data.hits.length
					? 'There are no more search results. Please try a new search.'
					: '';
				this.setState({
					results: res.data.hits,
					message: resultNotFoundMsg,
					loading: false,
				});
			})
			.catch((error) => {
				if (axios.isCancel(error) || error) {
					this.setState({
						loading: false,
						message: 'Failed to fetch results.Please check network',
					});
				}
			});
	};

	handleOnInputChange = (event) => {
		const query = document.getElementById('search-input').value;
		document.getElementById('modified-link').to = 'http://localhost:3000/?q='+query;
	};

	render() {
		return (
			<div className="container">
				<label className="search-label" htmlFor="search-input">
					<input
						type="text"
						id="search-input"
						placeholder="Search..."
					/>

					<Link id="modified-link" to='http://localhost:3000/?q='>
					<i className="fa fa-search search-icon" onClick={this.handleOnInputChange}/>
					</ Link>
				</label>

			</div>
			)
	}
}
export default Search;
