import React from 'react';
import Axios from 'axios';
import Table from 'react-bootstrap/Table'
//Internal
import '../../../All.css';

class AllItems extends React.Component{
  constructor( props ) {
		super( props );
		this.state = {
			query: '',
      results: [],
      loading: false,
      message: '',
		};
	}

  fetchSearchResults = (updatedPageNo = '', query ) => {
    const pageNumber = updatedPageNo ? `&page=${updatedPageNo}` : '';
    const searchUrl = `https://localhost:8080/mobiles/?s=${query}&offset=${pageNumber}`;    // By default the limit of results is 20

    if (this.cancel) {
      this.cancel.cancel();   // Cancel the previous request before making a new request
    }
    this.cancel = Axios.CancelToken.source();   // Create a new CancelToken

    Axios
      .get(searchUrl, {
        cancelToken: this.cancel.token,
      })
      .then((res) => {
        const resultNotFoundMsg = !res.data.hits.length
          ? 'There are no more search results. Please try a new search.'
          : '';
        this.setState({
          results: res.data,
          message: resultNotFoundMsg,
          loading: false,
        });
      })
      .catch((error) => {
        if (Axios.isCancel(error) || error) {
          this.setState({
            loading: false,
            message: 'Failed to fetch results.Please check network',
          });
        }
      });
  };

  handleOnInputChange = (event) => {
    const query = event.target.value;
    if ( ! query ) {
      Axios.get('http://localhost:8080/mobiles/').then(res=>{
          const mobileData = res.data;
          this.setState({results:mobileData})
      })
    } 
    else {
      this.setState({ query, loading: true, message: '' }, () => {
        this.fetchSearchResults(1, query);
      });
    }
  };

  componentWillMount(){
      Axios.get('http://localhost:8080/mobiles/').then(res=>{
          const mobileData = res.data;
          this.setState({results:mobileData})
      })
  }

    render(){
      return(
        <div>
          <div className="container">
            <label className="search-label" htmlFor="search-input">
              <input
                type="text"
                id="search-input"
                placeholder="Search..."
                onChange={this.handleOnInputChange}
              />
              <i className="fa fa-search search-icon"/>
            </label>
          </div>

          <div>
            <Table align="center" width="100%" striped bordered hover size="sm">
              <tbody>
                <tr hover>
                  <td align="center"><img alt={this.state.results.name} src={this.state.results.image} width="150" height="200"/></td>
                  <td>
                    <u><h4 id="product-name">{this.state.results.brand} {this.state.results.model}
                    </h4></u>
                    <h5 id="product-description">{this.state.results.memories}</h5>
                    <h6 id="product-price">${this.state.results.price}</h6>
                  </td>
                </tr>
              </tbody>

            </Table>
          </div>
        </div>
      )}
}

export default AllItems;
