//Dependencies
import React, { Component } from 'react';
//Internals
import AllItems from './AllItems';

class Products extends Component {
  render() {
    return (
      <div className="items-wrapper">
        <style background-color="#282c34" height="70px" display = "flex" flex-direction="column" align-items="center" justify-content="center" font-size =  "calc(10px + 2vmin)" color= "white" padding= "20px" cursor="pointer">
          <h2>All Items</h2>
        </style>
      <AllItems />
      </div>
    );
  }
}

export default Products;
