import React from 'react';
import '../../All.css';
import PRODUCTS from '../Data';

var Filtered = PRODUCTS;
class Search extends  React.Component {

	constructor( props ) {
		super( props );
		this.state = {
			query: '',
                        results: {},
                        loading: false,
                        message: '',
		};
	}

	Filter = (query) => {
		var count = 0
		Filtered = []
		if(query){
			for(var i = 0; i < PRODUCTS.length; i++) {
				if(PRODUCTS[i].name.includes(query)){
					Filtered[0] = PRODUCTS[i];
					count = count +1;
				}
			}
		}
		else{
			Filtered = PRODUCTS;
		}
	  }

	handleOnClick = (event) => {
		const query = document.getElementById('search-input').value;
		this.Filter(query)
	};

	render() {
		return (
			<div className="container">
				<label className="search-label" htmlFor="search-input">
					<input
						type="text"
						id="search-input"
						placeholder="Search..."
					/>
					<i className="fa fa-search search-icon" onClick={this.handleOnClick}/>
				</label>

			</div>
			)
	}
}
export default Search;
export {Filtered};
