//Dependencies
import React from 'react';
import { Icon } from 'react-materialize';
import Table from 'react-bootstrap/Table'
import map from 'lodash/map';
import ReactSearchBox from 'react-search-box';
//Internals
import PRODUCTS from '../../Data';
import '../../All.css';

const AllItems = () => (

  <body>

    <div className = "search">
      <label className="search-label" htmlFor="search-input">
				<input
					type="text"
					value=""
					id="search-input"
					placeholder="Search..."
				/>
				<i className="fa fa-search search-icon"/>
			</label>
    </div>
    <div>
      {map(PRODUCTS, (product)=> (
        <Table align="center" width="100%" striped bordered hover size="sm" striped>
          <tbody>
            <tr hover>
              <td align="center"><img alt={product.name} src={product.img} width="150" height="200"/></td>
              <td>
                <u><h4 id="product-name">{product.name}</h4></u>
                <h5 id="product-description">{product.description}</h5>
                <h6 id="product-price">${product.price}</h6>
                <Icon small id="add-icon">add_shopping_cart</Icon>
              </td>
            </tr>
          </tbody>
        </Table>
      ))}
    </div>
  </body>
)

export default AllItems;
