//Dependencies
import React from 'react';
import { Icon } from 'react-materialize';
import Table from 'react-bootstrap/Table'
import map from 'lodash/map';
//Internals
import PRODUCTS from '../../Data';
import {useLocation} from "react-router-dom";
import _ from 'lodash';

function Filter(){
  let query = new URLSearchParams(useLocation().search);
  var quer = query.get("q");
  return PRODUCTS.name.includes(quer)
}

const AllItems = () => (

  <body>

    <div>
      {map(PRODUCTS, (product)=> (
        <Table align="center" width="100%" striped bordered hover size="sm">
          {Filter ?
              <tbody>
                <tr hover>
                  <td align="center"><img alt={product.name} src={product.img} width="150" height="200"/></td>
                  <td>
                    <u><h4 id="product-name">{product.name}</h4></u>
                    <h5 id="product-description">{product.description}</h5>
                    <h6 id="product-price">${product.price}</h6>
                    <Icon small id="add-icon">add_shopping_cart</Icon>
                  </td>
                </tr>
              </tbody>
              :<tbody></tbody>
          }
        </Table>
      ))}
    </div>
  </body>
)

export default AllItems;
